import React, { useState, useEffect } from "react";
import { Helmet } from "react-helmet";
const Shop =()=>{
    const cardArr = [
        {
            id: 1,
            text: "Hey this is a test.",
            img: " ",
            like: 0
        },
        {
            id: 2,
            text: "If this works I'll call it a day.",
            img: " ",
            like: 0
        },
        {
            id: 3,
            text: "I'll drink a lot of beer.",
            img: " ",
            like: 0
        },
        {
            id: 4,
            text: "Cheers",
            img: " ",
            like: 0
        
        }]
        const [like, setState] = useState(0);
        const [Id, setId] = useState();
        const IncrementItem = (value) => {
            setState(like + 1 );
            setId(e.target.value)
          }
          const DecreaseItem = () => {
            setState(like - 1 );
          }
    return(
        <>
          <Helmet>
             <title>Arizona Resources - Acclaimed Home Warranty : Acclaimed Home Warranty</title>
             <meta name="description" content="Arizona Resources - Acclaimed Home Warranty" />
          </Helmet>
        <div>
            <h1>Shop</h1>
            
           { cardArr.map((card) => 
        <ul key={card.id}>
            <p>Like Button-- {Id}</p>
            <button value={card.id} onClick={() => IncrementItem(card.id)}>Like</button>
            <p>Dilike Button</p>
            <button onClick={() => DecreaseItem(card.id)}>Dislike</button>
            <p>Likes: {like}</p>
       
    
        </ul>
    )}
            
        </div>
        </>
    )
}
export default Shop;
