import React from "react";
import ReactDOM from "react-dom";

class ProductFilter extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      product: ""
    };
  }

  handleChangeCourse = event => {
    this.setState({ product: event.target.value });
  };

  getUnique(arr, comp) {
    const unique = arr
      //store the comparison values in array
      .map(e => e[comp])

      // store the keys of the unique objects
      .map((e, i, final) => final.indexOf(e) === i && i)

      // eliminate the dead keys & store unique objects
      .filter(e => arr[e])

      .map(e => arr[e]);

    return unique;
  }

  componentDidMount() {
 
    const productss = require("../data.json");
    const products = productss.productList;
    this.setState({ products: products });
  }

  render() {
    const uniqueCouse = this.getUnique(this.state.products, "unitsize");
    const products = this.state.products;
    const product = this.state.product;
    const filterDropdown = products.filter(function(result) {
      return result.unitsize === product;
    });

    return (
      <div>
      <br></br>
          <label>
            Looping through Courses tag from Json File
            <select
              value={this.state.product}
              onChange={this.handleChangeCourse}
            >
              {uniqueCouse.map(product => (
                <option key={product.id} value={product.unitsize}>
                  {product.unitsize}
                </option>
              ))}
            </select>
          </label>
          <div className="option-cont table-right">
          {filterDropdown.map(product => ( 
                  <div className="option" key={product.id}>
                    <div className="star-cont">
                     
                    </div>
                    <div className="body">
                      <h6 className="upper">{product.name}</h6>
                      <h4 className="lato">{product.price}
                        <span style={{ fontSize: "0.4em" }}>/MO</span></h4>
                      <h5 className="lato">{product.yearly}</h5>
                      {/* <input className="btn" type="submit" value="Buy Now" onClick={onClick} /> */}
                    </div>
                  </div>
                ))}

            </div>
          <div>
            {filterDropdown.map(product => (
              <div key={product.id} style={{ margin: "10px" }}>
                {product.name}
                <br />
              </div>
            ))}
          </div>
      </div>
    );
  }
}
export default ProductFilter;
