import React from "react";

class Shop extends React.Component {
   
    state = {
        products: []
      };
     
      
    componentDidMount() {
       let showId  = 8
        fetch('http://replatform.acclaimedhw.com/replatform/api/get_hoproducts_covup/'+ `${showId}`)
        .then((response) => response.json())
        .then(productList => {
            this.setState({ products: productList });
        });
      }
    onChange = (index, val) => {
      this.setState({
        products: this.state.products.map((product, i) => (
          i === index ? {...product, count: val} : product
         
        ))
      })
    }
  
    render () {
      return (
        <div>
          <ProductList products={this.state.products} onChange={this.onChange} />
          <Total products={this.state.products} />
        </div>
      )
    }
  };
  
  const ProductList = ({ products, onChange }) => (
    <div className="upgrade_list">
      {products.map((product, i) => (
        <div key={i} className="upgrade">
         
          <input 
            type="number" 
            value={product.count}
            onChange={e => onChange(i, parseInt(e.target.value) || 0)}
          />
          <span className="price">{product.yearly_price}</span> 
          <span className="name">{product.name}</span>
          <h5>{product.count}</h5>
        </div>
      ))}
    </div>
  );
  
  const Total = ({ products }) => (
    <h3>
      Price: 
      {products.reduce((sum, i) => (
        sum += i.count * i.price
      ), 0)}
      
    </h3>
  )
  
  export default Shop;
