import React, { useState, useEffect } from "react";
const Shop = () => {

    
      const products = [
        {title: 'Apple', count: 0, price: 100},
        {title: 'IBM', count: 0, price: 200},
        {title: 'HP', count: 0, price: 300},
      ]
   
    const [product, setState] = useState([]);
    const onChange = (index, val) => {
      setState(
        products.map((product, i) => (
          i === index ? {...product, count: val} : product
         
        ))
      )
    }
    const ProductList = ({ products, onChange }) => (
        <ul className="container">
          {products.map((product, i) => (
            <li key={i}>
              {product.title}
              <input 
                type="number" 
                value={product.count}
                onChange={e => onChange(i, parseInt(e.target.value) || 0)}
              />
              <h5>{product.count}</h5>
            </li>
          ))}
        </ul>
      );
    
      return (
        <div>
          <ProductList products={this.state.products} onChange={this.onChange} />
          <Total products={this.state.products} />
        </div>
      )
 

  
 
  
  const Total = ({ products }) => (
    <h3>
      Price: 
      {products.reduce((sum, i) => (
        sum += i.count * i.price
      ), 0)}
      
    </h3>
  )
      }
  export default Shop;
