import React, { useState, useEffect } from "react";
import { Helmet } from "react-helmet";
const Shop = () => {
const Card = ({ card, onLike, onDeslike }) =>
  <div>
    {card.text}
    <p>Likes: {card.like}</p>
    <button onClick={() => onLike(card.id)}>Like</button>
    <button onClick={() => onDeslike(card.id)}>Deslike</button>
  </div>

      const cards= [
        {
          id: 1,
          text: 'Hey this is a test.',
          img: ' ',
          like: 0,
        },
        {
          id: 2,
          text: "If this works I'll call it a day.",
          img: ' ',
          like: 0,
        },
        {
          id: 3,
          text: "I'll drink a lot of beer.",
          img: ' ',
          like: 0,
        },
        {
          id: 4,
          text: 'Cheers',
          img: ' ',
          like: 0,
        },
      ]


  

  const [data, setState] = useState([]);
  const handleLike= (id) => {
    setState(prevState => ({
      ...prevState,
      cards: prevState.cards.map(card => ({
        ...card,
        like: card.id === id ? card.like + 1 : card.like
      }))
    }))
  }

  const handleDeslike = (id) => {
    setState(prevState => ({
      ...prevState,
      cards: prevState.cards.map(card => ({
        ...card,
        like: card.id === id ? card.like - 1 : card.like
     }))
    }))
  }


    return (
      <div>
        <ul>
          {cards.map(card =>
            <li key={card.id}>
              <Card
                card={card}
                onLike={handleLike}
                onDeslike={handleDeslike}
              />
            </li>
          )}
        </ul>
       
      </div>
    )
}
export default Shop;
