import { Helmet } from "react-helmet";
const Shop =()=>{
    const cardArr = [
        {
            id: 1,
            text: "Hey this is a test.",
            img: " ",
            like: 0
        },
        {
            id: 2,
            text: "If this works I'll call it a day.",
            img: " ",
            like: 0
        },
        {
            id: 3,
            text: "I'll drink a lot of beer.",
            img: " ",
            like: 0
        },
        {
            id: 4,
            text: "Cheers",
            img: " ",
            like: 0
        
        }]
        const [like, setState] = useState(0);
        // const IncrementItem = () => {
        //     setState(like + 1 );
        //   }
        //   const DecreaseItem = () => {
        //     setState(like - 1 );
        //   }
        const IncrementItem = (id) => {
            let cards = cardArr;
            cards.find(c => c.id === id).like++;
            setState(cards);
        }
        const DecreaseItem = (id) => {
            let cards = cardArr;
            cards.find(c => c.id === id).like--;
            setState(cards);
        }  
    return(
        <>
          <Helmet>
             <title>Arizona Resources - Acclaimed Home Warranty : Acclaimed Home Warranty</title>
             <meta name="description" content="Arizona Resources - Acclaimed Home Warranty" />
          </Helmet>
        <div>
            <h1>Shop</h1>
           { cardArr.map((card) => 
        <ul key={card.id}>
            <p>Like Button</p>
            <button onClick={(card) => IncrementItem(card.id)}>Like</button>
            <p>Dilike Button</p>
            <button onClick={(card) => DecreaseItem(card.id)}>Dislike</button>
            <p>Likes: {cards}</p>
       
    
        </ul>
    )};
            
        </div>
        </>
    )
}
export default Shop;
