import React, { useState, useEffect } from 'react';
import APIUrl from "../Api"
import Carousel from 'react-bootstrap/Carousel'
import image1 from "./../assets/images/home-page-slider-01.png";
import image2 from "./../assets/images/home-page-slider-02.png";
import image3 from "./../assets/images/home-page-slider-03.png";
import image4 from "./../assets/images/home-page-slider-04.png";

const Homeslider = () => {
  const sliderImage = (APIUrl.defaults.assetsURL)
  const [sliderdata, setSlider] = useState([]);
  useEffect(async () => {
    const result = await APIUrl.get(`/get_sliders`)
    setSlider(result.data);
    console.log(result.data)
    console.log(sliderImage)
  }, []);
  return (
    <>
      <Carousel fade={true}>
        {sliderdata.map(item => (
          <Carousel.Item key={item.id}>
            <img
              className="d-block w-100"
              src={`${sliderImage} / ${item.image}`}
              alt="First slide"
            />
            <Carousel.Caption>
              <div className="container first-slid">
                <h1>{item.slide_title}</h1>
                <h3>{item.slide_content}</h3>
              </div>
            </Carousel.Caption>
          </Carousel.Item>
        ))}
      </Carousel>
    </>
  );
}
export default Homeslider;
