let cardArr = [
{
    id: 1,
    text: "Hey this is a test.",
    img: " ",
    like: 0
},
{
    id: 2,
    text: "If this works I'll call it a day.",
    img: " ",
    like: 0
},
{
    id: 3,
    text: "I'll drink a lot of beer.",
    img: " ",
    like: 0
},
{
    id: 4,
    text: "Cheers",
    img: " ",
    like: 0

}
]


export class Shop extends Component {
    constructor(props) {
    super(props);
    this.state = {
       cardArr:cardArr 
    };
     // this.handleClick = this.handleClick.bind(this);


  }



IncrementItem = (id) => {
    let cards = this.state.cardArr;
    cards.find(c => c.id === id).like++;
    this.setState({cardArr:cards});
}
DecreaseItem = (id) => {
    let cards = this.state.cardArr;
    cards.find(c => c.id === id).like--;
    this.setState({cardArr:cards});
}  

render() {

    this.state.cardArr.map((card) => 

        <ul>
            <li>
            <div key={card.id}>
                {card.text};
                <img src={card.img}/>
                <p>Like Button</p>
                <button onClick={() => this.handleClickAdd(card.id)}>Like</button>
                <p>Dilike Button</p>
                <button onClick={() => this.DecreaseItem(card.id)}>Dislike</button>
                <p>Likes: {card.like}</p>
