import React from "react";
import ReactDOM from "react-dom";

class AboutUs extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      product: ""
    };
  }

  handleChangeproduct = event => {
    this.setState({ product: event.target.value });
  };

  getUnique(arr, comp) {
    const unique = arr
      //store the comparison values in array
      .map(e => e[comp])

      // store the keys of the unique objects
      .map((e, i, final) => final.indexOf(e) === i && i)

      // eliminate the dead keys & store unique objects
      .filter(e => arr[e])

      .map(e => arr[e]);

    return unique;
  }

  componentDidMount() {
    const productss = require("./data.json");
    const products = productss.productList;
    this.setState({ products: products });
  }

  render() {
    const uniqueCouse = this.getUnique(this.state.products, "tag");
    const products = this.state.products;
    const product = this.state.product;
    const filterDropdown = products.filter(function(result) {
      return result.unitsize === product;
    });

    return (
      <div>
      <br></br>
          <label>
            Looping through products tag from Json File
            <select
              value={this.state.product}
              onChange={this.handleChangeproduct}
            >
              {uniqueCouse.map(product => (
                <option key={product.id} value={product.tag}>
                  {product.tag}
                </option>
              ))}
            </select>
          </label>
          <div>
            {filterDropdown.map(product => (
              <div key={product.id} style={{ margin: "10px" }}>
                {product.product}
                <br />
              </div>
            ))}
          </div>
      </div>
    );
  }
}
export default AboutUs;
