import React from 'react';

export default function Cart({ cart, setCart, value }) {
  const getTotalSum = () => {
    return cart.reduce(
        
      (sum, { yearly_price }) => sum + yearly_price,
      ""
    );
  };

  const clearCart = () => {
    setCart([]);
  };



  return (
    <>
      <h4>Cart</h4>
      {/* {cart.length > 0 && (
        <button onClick={clearCart}>Clear Cart</button>
      )} */}
      <div className="products">
      {cart.map((product, index) => (
                <div className="option" key={index}>
                    <ul><li>{product.name}</li><li></li><li></li><li></li><li></li><li></li></ul>
                </div>
              ))}
      </div>
      {value == 1 ? <span>400 /YR</span> : null}
      <div>Total Cost: {getTotalSum()}</div>
    </>
  );
}
